
$(document).ready(function (){

    //  mobile menue toggle
    $(document).on('click', '.menu-bar', function (){
        $('.menu-hidden').toggle("slide");
    });

    // paystack api
    const paymentForm = document.getElementById('paymentForm');
    paymentForm.addEventListener("submit", payWithPaystack, false);
    function payWithPaystack(e) {
    e.preventDefault();
    let handler = PaystackPop.setup({
        key: 'pk_test_0e18302d908150c92c7d338c967ab1ef6b475b78', // Replace with your public key
        email: document.getElementById("email-address").value,
        amount: document.getElementById("amount").value * 100,
        ref: ''+Math.floor((Math.random() * 1000000000) + 1), // generates a pseudo-unique reference. Please replace with a reference you generated. Or remove the line entirely so our API will generate one for you
        // label: "Optional string that replaces customer email"
        onClose: function(){
        alert('Window closed.');
        },
        callback: function(response){
            var reference = response.reference;
            $.ajax({
                
                type: 'GET',
                url: '/logged-in/investment/pay/verify-payment/'+response.reference,
                dataType: 'json',
                success: function (response) {
                    if (response.status == true) {
                        $.ajax({
                            type: 'POST',
                            url: '/logged-in/investment/pay/verify-payment',
                            dataType: 'json',
                            data: {
                                'id': $('#id').val(),
                                'investment_id': $('#investment_id').val(),
                                'reference': reference,
                                'amount': $('#amount').val(),
                                'slot': ($('#slot').val() != '' || $('#slot') != null ? $('#slot').val() : null),
                                'start_date': $('#start_date').val(),
                                'end_date': $('#end_date').val(),
                                'isPaid': 1,
                                'isActive': 1,
                            },
                            headers: {
                                'X-CSRF-TOKEN': $('meta[name="csrf-token"]').attr('content')
                            },
                            success: function (data) {
                                window.location = '/logged-in/investment/pay/verify-user-investment/'+reference;
                            },
                            error: function (data) {
                                alert('Transaction failed, try again');
                            },

                        });
                    } else {
                        alert('Payment failed, try agin');
                    }
                },
                error: function () {
                    alert('Failed');
                }
            });
            // window.location = 'https://api.paystack.co/transaction/verify/'+response.reference;
        // let message = 'Payment complete! Reference: ' + response.reference + 'and Status' + response.status;
        // alert(message);
        }
    });
    handler.openIframe();
    }

    // ajx function for invest
    // $(document).on('click', '#invest', function(e){
    //     e.preventDefault();
    //     alert('Clicked');
    // });
});